        var minder = this;

        // 
        var PRIORITY_COLORS = ['', '#A92E24', '#29A6BD',
            '#1E8D54', '#eb6100', '#876DDA', '#828282',
            '#828282', '#828282', '#828282'
        ];
        var PRIORITY_DATA = 'priority';

