    //
    Minder.Selection = kity.createClass( 'Selection', {
        base: kity.Rect,
        constructor: function ( height, color, width ) {
            this.callBase();
            this.height = height || 20;
            this.setAttr('id','_kity_selection');
            this.width = 2;
            this.fill('rgb(27,171,255)');
            this.setHide();
            this.timer = null;
            this.collapsed = true;
            this.startOffset = this.endOffset = 0;
            this.setOpacity(0.5);
            this.setStyle('cursor','text');
            this._show = false;

        },
        setColor:function(color){
            this.fill(color);
        },
        collapse : function(toStart){
            this.setOpacity(1);
            this.width = 2;
            this.collapsed = true;
            if(toStart){
                this.endOffset = this.startOffset;

            }else{
                this.startOffset = this.endOffset;
            }
            return this;
        },
        setStartOffset:function(offset){
            this.startOffset = offset;
            if(this.startOffset >= this.endOffset){
                this.collapse(true);
                return this;
            }
            this.collapsed = false;
            this.setOpacity(0.5);
            return this;
        },
        setEndOffset:function(offset){
            this.endOffset = offset;
            if(this.endOffset <= this.startOffset){
               this.startOffset = offset;
               this.collapse(true);
                return this;
            }
            this.collapsed = false;
            this.setOpacity(0.5);
            return this;
        },
        updateShow : function(offset,width){
            if(width){
                this.setShowHold();
            }
            this.setPosition(offset).setWidth(width);
            this.bringTop();
            return this;
        },
        setPosition: function ( offset ) {
            try {
                //  0.5  SVG 
                this.x = Math.round(offset.x) - 0.5;
                this.y = Math.round(offset.y) - 1.5;

            } catch ( e ) {
               console.log(e);
            }
            this.update();
            return this;
        },
        setHeight: function ( height ) {
            this.height = Math.round(height) + 2;
            return this;
        },
        setHide: function () {
            clearInterval( this.timer );
            this.setStyle( 'display', 'none' );
            this._show = false;
            return this;
        },
        setShowHold: function () {
            clearInterval( this.timer );
            this.setStyle( 'display', '' );
            this._show = true;
            return this;
        },
        setShow: function () {
            clearInterval( this.timer );
            var me = this,
                state = '';

            me.setStyle( 'display', '' );
            me._show = true;
            if(this.collapsed){
                me.setOpacity(1);
                this.timer = setInterval( function () {
                    me.setStyle( 'display', state );
                    state = state ? '' : 'none';
                }, 400 );
            }
            return this;
        },
        isShow:function(){
            return this._show;
        },
        isHide:function(){
            return !this._show;
        }
    } );
/* src/module/editor.selection.js end */




